sysauto_script.script:
// Test if NeedItUtils property is accessible
var niUtils = new NeedItUtils();
gs.info("autoCloseOverdue: " + niUtils.howLongToClose);
// Query all NeedIt Tasks which are not closed (state < 3) and which due date is more than howLongToClose (application property) days in the past
var overdueNITasks = new GlideRecord("x_58872_needit_needit_task");
overdueNITasks.addQuery("due_date", "<=", gs.daysAgo(niUtils.howLongToClose));
overdueNITasks.addQuery("state", "<", "3");
overdueNITasks.query();
while (overdueNITasks.next()){
	gs.info("Closing overdue NeedIt Task: " + overdueNITasks.number);
	overdueNITasks.state = 4;
	overdueNITasks.work_notes = "Record closed because it was too far past the Due date.";
	overdueNITasks.update();
}
